import React from"react";import{screen,render,waitForElementToBeRemoved}from"@testing-library/react";import Output from"./Output";import{TodoProvider}from"../context";import{ToastContainer}from"react-toastify";afterEach(()=>{jest.clearAllMocks(),jest.resetAllMocks()});let mockData=[{todo_id:1,todo:"Test Todo"}];describe("Test the output",()=>{beforeEach(()=>{jest.spyOn(global,"fetch").mockResolvedValue({ok:!0,json:jest.fn().mockResolvedValue(mockData)})}),it("greps the todoText",async()=>{render(React.createElement(TodoProvider,null,React.createElement(Output,null),React.createElement(ToastContainer,null))),await waitForElementToBeRemoved(screen.getByTestId("loader"));var e=await screen.findByTestId("todoText");expect(e).toBeInTheDocument()})});